<script>
  const slider = document.getElementById('priceSlider');
  const sliderValueDisplay = document.getElementById('sliderValue');
  const labels = document.querySelectorAll('.slider-label');

  // Update slider track color and displayed value
  function updateSlider(value) {
    const percent = (value / slider.max) * 100;
    sliderValueDisplay.textContent = `£${value}`;
    sliderValueDisplay.style.left = `${percent}%`;

    // Update track color
    slider.style.background = `linear-gradient(to right, #0081DC 0%, #0081DC ${percent}%, #ddd ${percent}%, #ddd 100%)`;

    // Update active label
    labels.forEach(label => {
      const labelValue = parseInt(label.getAttribute('data-value'));
      // Find the closest label to the current value
      if (value >= labelValue) {
        labels.forEach(l => l.classList.remove('active'));
        label.classList.add('active');
      }
      // Special case for maximum value
      if (value === 1000) {
        labels.forEach(l => l.classList.remove('active'));
        document.querySelector('.slider-label[data-value="1000"]').classList.add('active');
      }
    });
  }

  // Initialize
  updateSlider(parseInt(slider.value));

  // Event listener for slider changes
  slider.addEventListener('input', function() {
    updateSlider(parseInt(this.value));
  });

  // Click on labels to set slider value
  labels.forEach(label => {
    label.addEventListener('click', function() {
      const value = parseInt(this.getAttribute('data-value'));
      slider.value = value;
      updateSlider(value);
    });
  });
</script>

<script>
  const initSlider = () => {
    const imageList = document.querySelector(".slider-wrapper .image-list");
    const slideButtons = document.querySelectorAll(".slider-wrapper .slide-button");
    const sliderScrollbar = document.querySelector(".container .slider-scrollbar");
    const scrollbarThumb = sliderScrollbar.querySelector(".scrollbar-thumb");
    const maxScrollLeft = imageList.scrollWidth - imageList.clientWidth;

    // Handle scrollbar thumb drag
    scrollbarThumb.addEventListener("mousedown", (e) => {
      const startX = e.clientX;
      const thumbPosition = scrollbarThumb.offsetLeft;
      const maxThumbPosition = sliderScrollbar.getBoundingClientRect().width - scrollbarThumb.offsetWidth;

      // Update thumb position on mouse move
      const handleMouseMove = (e) => {
        const deltaX = e.clientX - startX;
        const newThumbPosition = thumbPosition + deltaX;
        // Ensure the scrollbar thumb stays within bounds
        const boundedPosition = Math.max(0, Math.min(maxThumbPosition, newThumbPosition));
        const scrollPosition = (boundedPosition / maxThumbPosition) * maxScrollLeft;

        scrollbarThumb.style.left = `${boundedPosition}px`;
        imageList.scrollLeft = scrollPosition;
      }
      // Remove event listeners on mouse up
      const handleMouseUp = () => {
        document.removeEventListener("mousemove", handleMouseMove);
        document.removeEventListener("mouseup", handleMouseUp);
      }
      // Add event listeners for drag interaction
      document.addEventListener("mousemove", handleMouseMove);
      document.addEventListener("mouseup", handleMouseUp);
    });
    // Slide images according to the slide button clicks
    slideButtons.forEach(button => {
      button.addEventListener("click", () => {
        const direction = button.id === "prev-slide" ? -1 : 1;
        const scrollAmount = imageList.clientWidth * direction;
        imageList.scrollBy({
          left: scrollAmount,
          behavior: "smooth"
        });
      });
    });
    // Show or hide slide buttons based on scroll position
    const handleSlideButtons = () => {
      slideButtons[0].style.display = imageList.scrollLeft <= 0 ? "none" : "flex";
      slideButtons[1].style.display = imageList.scrollLeft >= maxScrollLeft ? "none" : "flex";
    }
    // Update scrollbar thumb position based on image scroll
    const updateScrollThumbPosition = () => {
      const scrollPosition = imageList.scrollLeft;
      const thumbPosition = (scrollPosition / maxScrollLeft) * (sliderScrollbar.clientWidth - scrollbarThumb.offsetWidth);
      scrollbarThumb.style.left = `${thumbPosition}px`;
    }
    // Call these two functions when image list scrolls
    imageList.addEventListener("scroll", () => {
      updateScrollThumbPosition();
      handleSlideButtons();
    });
  }
  window.addEventListener("resize", initSlider);
  window.addEventListener("load", initSlider);
</script>


